def create_faq_questions():
    """–°—Ç–≤–æ—Ä—é—î –∞–±–æ –æ–Ω–æ–≤–ª—é—î FAQ –∑–∞–ø–∏—Å–∏."""
    created_count = 0
    updated_count = 0

    for faq_data in FAQ_DATA:
        faq, created = FAQ.objects.update_or_create(
            question_uk=faq_data['question_uk'],
            defaults={
                'question_ru': faq_data.get('question_ru', ''),
                'answer_uk': faq_data['answer_uk'],
                'answer_ru': faq_data.get('answer_ru', ''),
                'order': faq_data['order'],
                'is_active': faq_data['is_active']
            }
        )

        if created:
            created_count += 1
            print(f"‚úì –°—Ç–≤–æ—Ä–µ–Ω–æ: {faq.question_uk}")
        else:
            updated_count += 1
            print(f"‚Üª –û–Ω–æ–≤–ª–µ–Ω–æ: {faq.question_uk}")

    print(f"\n‚úÖ –ì–æ—Ç–æ–≤–æ! –°—Ç–≤–æ—Ä–µ–Ω–æ: {created_count}, –æ–Ω–æ–≤–ª–µ–Ω–æ: {updated_count}")
    print(f"üìä –í—Å—å–æ–≥–æ –∞–∫—Ç–∏–≤–Ω–∏—Ö FAQ: {FAQ.objects.filter(is_active=True).count()}")

if __name__ == '__main__':
    create_faq_questions()


